RLWKA   =    RLWKE+1        #WORKING SECTION GLOBALS ADJUSTMENT} EQU RLWKE+1
RLWKS   =    RLWKA+1        #WORKING SECTION GLOBALS START} EQU RLWKA+1
RLCNE   =    RLWKS+1        #CONSTANTS SECTION END} EQU RLWKS+1
