#                            .
#                            SAVED VALUE OF LAST ARGUMENT
#                            SAVED VALUE OF FIRST LOCAL
#                            .
#                            SAVED VALUE OF LAST LOCAL
#                            SAVED VALUE OF FUNCTION NAME
#                            SAVED CODE BLOCK PTR (R$COD)
#                            SAVED CODE POINTER (-R$COD)
#                            SAVED VALUE OF FLPRT
#                            SAVED VALUE OF FLPTR
