PNTH8:  PUSH ESI            #STACK SUBJECT POINTER} MOV XL -(XS)
#
#      HERE TO OBEY NEXT CODE WORD
#
PNTH9:  MOV  EDI,[EBP]      #GET NEXT CODE WORD} LCW XR
        ADD  EBP,4
        JMP  DWORD PTR [EDI] #EXECUTE NEXT CODE WORD} BRI (XR)
                            #} EJC
        .eject
#
#      POS (INTEGER ARGUMENT)
