BLKLN:                      #ENTRY POINT} PRC E 0
        .global BLKLN
        MOV  ESI,ECX        #COPY FIRST WORD} MOV WA XL
        MOVZX ESI,BYTE PTR [ESI-1] #GET ENTRY ID (BL_XX)} LEI XL
        CMP  ESI,BL___      #SWITCH ON BLOCK TYPE} BSW XL BL___ BLN00
        JGE  BLN00
        JMP  DWORD PTR L0006[ESI*4]
