        .long BLN01         #ARBLK} IFF BL_AR BLN01
        .long BLN12         #CDBLK} IFF BL_CD BLN12
        .long BLN12         #EXBLK} IFF BL_EX BLN12
        .long BLN07         #ICBLK} IFF BL_IC BLN07
        .long BLN03         #NMBLK} IFF BL_NM BLN03
