#      RETURN
#
CMP30:  MOV  EDI,DWORD PTR (4*CMTRA)[ESP] #LOAD INITIAL ENTRY CDBLK POINTER} MOV CMTRA(XS) XR
        ADD  ESP,4*CMNEN    #POP WORK LOCATIONS OFF STACK} ADD *CMNEN XS
        RET                 #AND RETURN TO CMPIL CALLER} EXI
