#        OR   EBX,EBX        #OK IF NON-NULL FIELD} BNZ WB CMP18  (Optimized)
        JNZ  CMP18
        CALL ERR_0          #} ERB 219 Syntax error: Empty goto field
        .byte 219
#
#      CONTROL CARD FOUND
#
CMP32:  INC  EBX            #POINT PAST CH_MN} ICV WB
        CALL CNCRD          #PROCESS CONTROL CARD} JSR CNCRD
