        OR   EDI,EDI        #IF NOT} BZE XR IOPA7
        JZ   IOPA7
        MOV  ESI,DWORD PTR (4*VRVAL)[EDI] #POINT TO TRBLK} MOV VRVAL(XR) XL
        MOV  EAX,DWORD PTR (4*VRVAL)[ESI] #UNSPLICE IT} MOV VRVAL(XL) VRVAL(XR)
        MOV  DWORD PTR (4*VRVAL)[EDI],EAX
