        XOR  EDI,EDI        #SET OFFSET TO FIRST SET OF BOUNDS} ZER XR
        MOV  EBX,DWORD PTR (4*ARNDM)[ESI] #LOAD COUNT OF DIMS TO CONTROL LOOP} LCT WB ARNDM(XL)
        JMP  SHORT PRN19          #JUMP INTO PRINT LOOP} BRN PRN19 # (Jump shortened)
